mapper.select:

select proNum, proName, proPrice,proDes, proDate, proImg, proThumbImg
from product 
where proNum = #{proNum}

select * from product where proNum > 0
and proName like CONCAT('%',#{keyword},'%') and proDes like CONCAT('%',#{keyword},'%')
order by proNum desc , proDate desc limit #{pageStart},#{perPageNum}

select decode(count(*), 0, 'false', 'true') from cart
where proNum = #{proNum}
and memId = #{memId}
